import React, { Component } from 'react';
import './App.css';
import NewLyricDisplay from './NewLyricDisplay';
import NewBarButton from './NewBarButton';
import LyricList from './LyricList';


class App extends Component {
  constructor() {
    super();
    this.state = {
      randomIndex: 4
    }
    this.changeQuote = this.changeQuote.bind(this);
  }

  changeQuote() {
    this.setState({
        randomIndex: Math.floor(Math.random() * 8)
    });
}
render() {
  return (
    <div className="App">
      <NewLyricDisplay bar={LyricList[this.state.randomIndex]}/>
      <NewBarButton newBar={this.changeQuote}/>
    </div>
  );
}

}

export default App;
